import styled  from "styled-components";
import React, {useState, useEffect} from "react";
import axios from "axios";


export default function Msg({chan,msgs}){



    return (
        <Container5>
            <div className="msgs">
                {
                    msgs.map((msg)=>{
                        return (
                            msg.user === localStorage.getItem('user') ?
                            <div className={"msgsent"}>
                                <div className="content">
                                    <p>{msg.msg}</p>
                                </div>
                            </div>:
                            <div className="msgrec">
                                <div className="content">
                                    <p>{msg.msg}</p>
                                </div>
                            </div>
                        );
                    })
                }
            </div>
        </Container5>
    );
}

const Container5 =styled.div`
    height: 80%;
    .msgs{
        padding: 1rem 2rem;
        display: flex;
        flex-direction: column;
        gap: 1rem;
        overflow: auto;
        .msgsent{
            display: flex;
            align-items: center;
            justify-content: flex-end;
            .content {
                max-width: 40%;
                overflow-wrap: break-word;
                font-size: 1.1rem;
                background-color: white;
            }
        }
        .msgrec{
            display: flex;
            align-items: center;
            justify-content: flex-start;
            .content {
                max-width: 40%;
                overflow-wrap: break-word;
                font-size: 1.1rem;
                background-color: white;
            }
        }
    }
`;
