import styled  from "styled-components";
import React, {useState, useEffect} from "react";
import axios from "axios";


export default function Input({chan}){

    const [msgg, setmsg] = useState(undefined);

    const handleSubmit = async (event)=>{
        event.preventDefault();

        var msg = msgg;
        var index = chan;
        var user = localStorage.getItem("user");

        const { data } = await axios.post('http://localhost:8080/api/db/addmsg',{
            index,
            msg,
            user,
        });

        setmsg("");

    };

    const handleChange = (event)=> {
        setmsg(event.target.value );
    };

    return (
        <Container4>
            <div className="input">
                <input type="text" placeholder="" name="msg" onChange={(e)=>handleChange(e)} value={msgg}/>
            </div>
            <form className="submitForm" onSubmit={(event)=>handleSubmit(event)}> 
                <button type="submit">send</button> 
            </form>
        </Container4>
    );
}

const Container4 =styled.div`
    display: grid;
    grid-template-columns: 100%;
    align-items: center;
    background-color: #000000;

    .input {
        width: 100%;
        height 100%
        display: flex;
        background-color: #ffffff34;
        input {
            width: 100%;
            height: 100%;
            background-color: transparent;
            color: white;
            border: none;
            padding left: 1rem;
            font-size: 1.2rem;
            &::selection {
                background-color: #9186f3; 
            }
            &:focus {
                outline: none;
            }
        }
    }
    form {
        height: 100%;
        width: 100%;
        button {
            height: 100%;
            width: 100%;  
        }
    }
`;
