import styled  from "styled-components";
import React, {useState, useEffect} from "react";
import axios from "axios";


export default function Input({chan}){

    const [msgg, setmsg] = useState({
        msgg: "",
    });

    const handleSubmit = async (event)=>{

        var msg = msgg.msgg;
        var index = chan;

        const { data } = await axios.post('http://localhost:8080/api/db/addmsg',{
            index,
            msg,
        });

        alert("sent");

    };

    const handleChange = (event)=> {
        setmsg({ ...msgg,[event.target.name]:event.target.value })
    };

    return (
        <Container4>
            <form className="submitForm" onSubmit={(event)=>handleSubmit(event)}>
                <input type="text" placeholder="" name="msg" onChange={(e)=>handleChange(e)}/> 
                <button type="submit">send</button> 
            </form>
        </Container4>
    );
}

const Container4 =styled.div`
    display: grid;
    grid-template-columns: 100%;
    align-items: center;
    background-color: #000000;

    .submitForm {
        width: 100%;
        height 100%
        display: flex;
        background-color: #ffffff34;
        input {
            width: 100%;
            height: 100%;
            background-color: transparent;
            color: white;
            border: none;
            padding left: 1rem;
            font-size: 1.2rem;
            &::selection {
                background-color: #9186f3; 
            }
            &:focus {
                outline: none;
            }
        }
    }
`;
